import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import wishlistService from '../services/wishlist.service';
import { toast } from 'react-hot-toast';

const useWishlistStore = create(
  persist(
    (set, get) => ({
      wishlist: null,
      loading: false,

      // Fetch wishlist
      fetchWishlist: async () => {
        try {
          set({ loading: true });
          const response = await wishlistService.getWishlist();
          set({ wishlist: response.data, loading: false });
          return response.data;
        } catch (error) {
          console.error('Fetch wishlist error:', error);
          set({ loading: false });
          // Don't show error toast for unauthenticated users
          if (error.response?.status !== 401) {
            toast.error('Failed to load wishlist');
          }
          throw error;
        }
      },

      // Add to wishlist
      addToWishlist: async (productId) => {
        try {
          console.log('Adding to wishlist, productId:', productId);
          const response = await wishlistService.addToWishlist(productId);
          console.log('Add to wishlist response:', response);
          
          // Update state immediately with the response data
          set({ wishlist: response.data });
          toast.success('Added to wishlist');
          
          return response.data;
        } catch (error) {
          console.error('Add to wishlist error:', error);
          console.error('Error response:', error.response?.data);
          console.error('Error status:', error.response?.status);
          const message = error.response?.data?.message || 'Failed to add to wishlist';
          toast.error(message);
          throw error;
        }
      },

      // Remove from wishlist
      removeFromWishlist: async (productId) => {
        try {
          const response = await wishlistService.removeFromWishlist(productId);
          set({ wishlist: response.data });
          toast.success('Removed from wishlist');
          return response.data;
        } catch (error) {
          console.error('Remove from wishlist error:', error);
          toast.error('Failed to remove from wishlist');
          throw error;
        }
      },

      // Toggle wishlist (add or remove)
      toggleWishlist: async (productId) => {
        const { wishlist } = get();
        
        if (!wishlist || !wishlist.items) {
          return get().addToWishlist(productId);
        }
        
        const isInWishlist = wishlist.items.some(item => {
          if (typeof item.product === 'string') {
            return item.product === productId;
          }
          return item.product?._id === productId;
        });

        if (isInWishlist) {
          return get().removeFromWishlist(productId);
        } else {
          return get().addToWishlist(productId);
        }
      },

      // Check if product is in wishlist
      isInWishlist: (productId) => {
        const { wishlist } = get();
        if (!wishlist || !wishlist.items) return false;
        
        return wishlist.items.some(item => {
          // Handle both populated and non-populated product
          if (typeof item.product === 'string') {
            return item.product === productId;
          }
          return item.product?._id === productId;
        });
      },

      // Get wishlist item count
      getWishlistCount: () => {
        const { wishlist } = get();
        return wishlist?.items?.length || 0;
      },

      // Clear wishlist
      clearWishlist: async () => {
        try {
          await wishlistService.clearWishlist();
          set({ wishlist: { items: [] } });
          toast.success('Wishlist cleared');
        } catch (error) {
          console.error('Clear wishlist error:', error);
          toast.error('Failed to clear wishlist');
          throw error;
        }
      },

      // Move to cart
      moveToCart: async (productId) => {
        try {
          const response = await wishlistService.moveToCart(productId);
          set({ wishlist: response.data.wishlist });
          toast.success('Moved to cart');
          return response.data;
        } catch (error) {
          console.error('Move to cart error:', error);
          const message = error.response?.data?.message || 'Failed to move to cart';
          toast.error(message);
          throw error;
        }
      },

      // Reset wishlist (on logout)
      resetWishlist: () => {
        set({ wishlist: null, loading: false });
      }
    }),
    {
      name: 'wishlist-storage',
      partialize: (state) => ({ wishlist: state.wishlist })
    }
  )
);

export default useWishlistStore;
