import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import orderService from '../services/order.service';
import api from '../utils/api';
import useCartStore from '../store/useCartStore';
import { toast } from 'react-hot-toast';

const ShippingSchema = Yup.object().shape({
  addressLine1: Yup.string().required('Address is required'),
  city: Yup.string().required('City is required'),
  state: Yup.string().required('State is required'),
  country: Yup.string().required('Country is required'),
  zipCode: Yup.string().required('Zip code is required')
});

const CheckoutPage = () => {
  const navigate = useNavigate();
  const { items, subtotal, tax, total } = useCartStore();

  const [defaultAddress, setDefaultAddress] = React.useState(null);

  const initialValues = React.useMemo(() => ({
    fullName: defaultAddress?.fullName || '',
    phone: defaultAddress?.phone || '',
    addressLine1: defaultAddress?.addressLine1 || '',
    addressLine2: defaultAddress?.addressLine2 || '',
    city: defaultAddress?.city || '',
    state: defaultAddress?.state || '',
    country: defaultAddress?.country || '',
    zipCode: defaultAddress?.zipCode || '',
    paymentMethod: 'cod',
    notes: ''
  }), [defaultAddress]);

  // Fetch user's default address and prefill form
  React.useEffect(() => {
    const loadDefault = async () => {
      try {
        // suppress toast for optional endpoint (404 means no default)
        const res = await api.get('/addresses/default', { suppressToast: true });
        const address = res.data?.address || res.address || res.data;
        if (address) setDefaultAddress(address);
      } catch (err) {
        // ignore if no default address
      }
    };
    loadDefault();
  }, []);

  const handleSubmit = async (values, { setSubmitting }) => {
    if (!items || items.length === 0) {
      toast.error('Your cart is empty');
      return;
    }

    const orderPayload = {
      // Include items to satisfy validation middleware
      items: items.map(item => ({
        productId: item.product._id || item.productId,
        quantity: item.quantity,
        price: item.price
      })),
      shippingAddress: {
        fullName: values.fullName || defaultAddress?.fullName || '',
        phone: values.phone || defaultAddress?.phone || '',
        addressLine1: values.addressLine1,
        addressLine2: values.addressLine2,
        city: values.city,
        state: values.state,
        country: values.country,
        zipCode: values.zipCode
      },
      // Backend expects payment.method path
      payment: {
        method: values.paymentMethod
      },
      notes: values.notes
    };

    try {
      setSubmitting(true);
      const order = await orderService.createOrder(orderPayload);
      toast.success('Order placed successfully');
      navigate('/orders');
    } catch (error) {
        console.error('Create order error:', error);
        const serverMessage = error.response?.data?.message;
        const serverErrors = error.response?.data?.errors;
        if (serverErrors && Array.isArray(serverErrors)) {
          // Show first validation message
          toast.error(serverErrors[0].msg || 'Validation failed');
        } else {
          toast.error(serverMessage || 'Failed to place order');
        }
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow p-8">
        <h1 className="text-2xl font-bold mb-4">Checkout</h1>

        <div className="mb-6">
          <h2 className="text-lg font-semibold">Order Summary</h2>
          <p className="text-gray-600">Items: {items.length}</p>
          <p className="text-gray-600">Subtotal: ${subtotal.toFixed(2)}</p>
          <p className="text-gray-600">Tax: ${tax.toFixed(2)}</p>
          <p className="text-lg font-bold">Total: ${total.toFixed(2)}</p>
        </div>

        <Formik
          enableReinitialize
          initialValues={initialValues}
          validationSchema={ShippingSchema}
          onSubmit={handleSubmit}
        >
          {({ errors, touched, isSubmitting }) => (
            <Form>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {/* Display saved name/phone from default address (read-only) */}
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium">Deliver To</label>
                    <div className="mt-1 p-3 border rounded bg-gray-50">
                      <div className="font-medium">{defaultAddress ? defaultAddress.fullName : ''}</div>
                      <div className="text-sm text-gray-600">{defaultAddress ? defaultAddress.phone : ''}</div>
                    </div>
                  </div>
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium">Address Line 1</label>
                  <Field name="addressLine1" className="mt-1 w-full border px-3 py-2 rounded" />
                  {errors.addressLine1 && touched.addressLine1 && <div className="text-red-600 text-sm">{errors.addressLine1}</div>}
                </div>
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium">Address Line 2 (optional)</label>
                  <Field name="addressLine2" className="mt-1 w-full border px-3 py-2 rounded" />
                </div>
                <div>
                  <label className="block text-sm font-medium">City</label>
                  <Field name="city" className="mt-1 w-full border px-3 py-2 rounded" />
                  {errors.city && touched.city && <div className="text-red-600 text-sm">{errors.city}</div>}
                </div>
                <div>
                  <label className="block text-sm font-medium">State</label>
                  <Field name="state" className="mt-1 w-full border px-3 py-2 rounded" />
                  {errors.state && touched.state && <div className="text-red-600 text-sm">{errors.state}</div>}
                </div>
                <div>
                  <label className="block text-sm font-medium">Country</label>
                  <Field name="country" className="mt-1 w-full border px-3 py-2 rounded" />
                  {errors.country && touched.country && <div className="text-red-600 text-sm">{errors.country}</div>}
                </div>
                <div>
                  <label className="block text-sm font-medium">Zip Code</label>
                  <Field name="zipCode" className="mt-1 w-full border px-3 py-2 rounded" />
                  {errors.zipCode && touched.zipCode && <div className="text-red-600 text-sm">{errors.zipCode}</div>}
                </div>
              </div>

              <div className="mt-6">
                <h3 className="text-lg font-semibold mb-2">Payment Method</h3>
                <label className="flex items-center gap-2">
                  <Field type="radio" name="paymentMethod" value="cod" />
                  <span>Cash on Delivery (COD)</span>
                </label>
              </div>

              <div className="mt-6">
                <label className="block text-sm font-medium">Order Notes (optional)</label>
                <Field as="textarea" name="notes" className="mt-1 w-full border px-3 py-2 rounded" />
              </div>

              <div className="mt-6 flex items-center gap-4">
                <button type="submit" disabled={isSubmitting} className="bg-indigo-600 text-white px-6 py-3 rounded font-semibold">
                  {isSubmitting ? 'Placing Order...' : 'Place Order (COD)'}
                </button>
                <button type="button" onClick={() => navigate('/cart')} className="text-gray-600">
                  Back to Cart
                </button>
              </div>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
};

