import api from '../utils/api';

class CartService {
  // Get cart
  async getCart() {
    const response = await api.get('/cart');
    return response.data.cart;
  }

  // Add to cart
  async addToCart(productId, quantity = 1) {
    const response = await api.post('/cart/add', { productId, quantity });
    return response.data.cart || response.data.data?.cart;
  }

  // Update cart item
  async updateCartItem(productId, quantity) {
    const response = await api.put(`/cart/${productId}`, { quantity });
    return response.data.cart || response.data.data?.cart;
  }

  // Remove from cart
  async removeFromCart(productId) {
    const response = await api.delete(`/cart/${productId}`);
    return response.data.cart || response.data.data?.cart;
  }

  // Clear cart
  async clearCart() {
    const response = await api.delete('/cart');
    return response.data.cart || response.data.data?.cart;
  }

  // Sync cart
  async syncCart(items) {
    const response = await api.post('/cart/sync', { items });
    return response.data.cart || response.data.data?.cart;
  }

  // Local cart management (for non-logged-in users)
  getLocalCart() {
    const cartStr = localStorage.getItem('cart');
    return cartStr ? JSON.parse(cartStr) : { items: [], total: 0 };
  }

  saveLocalCart(cart) {
    localStorage.setItem('cart', JSON.stringify(cart));
  }

  addToLocalCart(product, quantity = 1) {
    const cart = this.getLocalCart();
    const existingItemIndex = cart.items.findIndex(item => item.productId === product._id);

    if (existingItemIndex > -1) {
      cart.items[existingItemIndex].quantity += quantity;
    } else {
      cart.items.push({
        productId: product._id,
        product: product,
        quantity: quantity,
        price: product.price
      });
    }

    cart.total = cart.items.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    this.saveLocalCart(cart);
    return cart;
  }

  removeFromLocalCart(productId) {
    const cart = this.getLocalCart();
    cart.items = cart.items.filter(item => item.productId !== productId);
    cart.total = cart.items.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    this.saveLocalCart(cart);
    return cart;
  }

  clearLocalCart() {
    localStorage.removeItem('cart');
  }
}

const cartServiceInstance = new CartService();
export default cartServiceInstance;
